import React from "react";
import ItemMusica from "./components/ItemMusica";
import api from "./api";

function Musicas() {

    const [musicas, setMusicas] = useState([]);

    function listar() {
        api
            .get()
            .then((respostaObtida) => {
                // cairá aqui se a requisição for realizada;
                console.log(respostaObtida);
                // objeto que representa a resposta enviada pela API;
                console.log(respostaObtida.status);
                // vendo status da resposta (OK - 200);
                console.log(respostaObtida.data);
                // vendo os dados da resposta (data: []);
                setMusicas(respostaObtida.data);
                // setando "musicas" com os mesmos dados recebidos pela resposta da
                // requisição;
            })
            .catch((erroOcorrido) => {
                // cairá aqui se houver algum erro durante a
                // requisição;
                console.log(erroOcorrido);
            });
    }

    return (
        <>
            <div className="container">
                <div className="filter">
                    <button className="btn">Adicionar</button>
                </div>
            </div>
            <div className="container">
                <div className="music-boxes">
                    {musicas.map((musica => 

                    <ItemMusica
                        nome={musica}
                        artista="SprintBreakers"
                        genero="Música da boa"
                        ano="2023"
                    />
                    ))}
                    <ItemMusica
                        nome="Alcateia"
                        artista="Blatta"
                        genero="Punk"
                        ano="2020"
                    />
                    <ItemMusica
                        nome="Xinxom"
                        artista="Pala"
                        genero="Rock"
                        ano="1990"
                    />
                </div>
            </div>
        </>
    )
}

export default Musicas;
